 输入毫秒，返回 以 “xx天xx小时xx分xx秒xx毫秒&rdquo; 格式的字符串。
function timeFormat(v) { var r; var S =1000, M = 60 * S, H = 60 * M, D = H * 24; function _parse(v,m,n){ return parseInt(v%m/n); } function ms(t) { return _result(_parse(t,S,1),&#39;毫秒&#39;); } function s(t){ return _result(_parse(t,M,S),&#39;秒&#39;); } function m(t){ return _result(_parse(t,H,M),&#39;分钟&#39;); } function h(t){ return _result(_parse(t,D,H),&#39;小时&#39;); } function d (t) { return _result(_parse(t,Infinity,D),&#39;天&#39;); } function _result(v,u){ return (v) ? v + &#39; &#39; + u + &#39; &#39; : &#39;&#39;; } return d(v) + h(v) + m(v) + s(v) + ms(v) ; };
